<template>
	<view>
		<view class="logo">
			<view class="img">
				<image
					mode="widthFix"
					src="data:image/png;base64,"
				></image>
			</view>
		</view>
		<!-- 账号密码输入框 -->
		<view class="form">
			<view class="username">
				<picker @change="bindPickerChange" :value="index" :range="array" range-key="name">
					<view class="uni-input">{{ array[index].name }}</view>
				</picker>
			</view>
			<!-- <view class="username"><input placeholder="请输入手机号" v-model="phoneNumber" placeholder-style="color: rgba(255,255,255,0.8);" /></view>
			<view class="password"><input placeholder="请输入密码" v-model="passwd" password="true" placeholder-style="color: rgba(255,255,255,0.8);" /></view> -->
			<!-- <view class="btn" @tap="toPark">选择园区</view> -->
			<view class="btn" @tap="doLogin">进 入 园 区</view>
		</view>
	</view>
</template>

<script>
export default {
	data() {
		return {
			array: [
				{
					id: 0,
					name: '园区加载中，请稍等。。',
					logo: 'http://obs-312.obs.cn-east-2.myhuaweicloud.com/20190426206c5f6d08.jpg',
					location: '330108',
					address: '聚业路28号',
					sort: 0,
					introduction: '环境好，风景优美',
					createBy: '黄鹏飞2号',
					createDate: '2019-04-19 21:04:03',
					updateBy: '黄鹏飞2号',
					updateDate: '2019-04-26 20:37:44',
					status: 1
				}
			],
			index: 0,
			phoneNumber: '',
			passwd: '',
			isShowOauth: false
		};
	},
	onShow() {},
	// beforeCreate() {
	// 	const s = document.createElement('script');
	// 	s.type = 'text/javascript';
	// 	s.src = 'http://pv.sohu.com/cityjson?ie=utf-8';
	// 	document.body.appendChild(s);
	// 	const wx = document.createElement('script');
	// 	wx.type = 'text/javascript';
	// 	wx.src = 'http://res.wx.qq.com/open/js/jweixin-1.0.0.js';
	// 	document.body.appendChild(wx);
	// },
	onLoad() {
		// uni.showToast({ title: '请确认已开启定位服务', icon: 'none' });
		////////////////wechat/////////////////
		const wx = require('jweixin-module');
		// 获取签名
		uni.request({
			url: this.$tempUrl + '/wechat/config',
			method: 'GET',
			success: res => {
				wx.config({
					debug: false, // 开启调试模式,调用的所有api的返回值会在客户端alert出来，若要查看传入的参数，可以在pc端打开，参数信息会通过log打出，仅在pc端时才会打印。
					appId: res.data.appId, // 必填，公众号的唯一标识
					timestamp: res.data.timestamp, // 必填，生成签名的时间戳
					nonceStr: res.data.nonceStr, // 必填，生成签名的随机串
					signature: res.data.signature, // 必填，签名
					jsApiList: ['getLocation'] // 必填，需要使用的JS接口列表
				});
				wx.ready(() => {
					wx.getLocation({
						type: 'wgs84', // 默认为wgs84的gps坐标，如果要返回直接给openLocation用的火星坐标，可传入'gcj02'
						success: res => {
							var latitude = res.latitude; // 纬度，浮点数，范围为90 ~ -90
							var longitude = res.longitude; // 经度，浮点数，范围为180 ~ -180。
							console.log(res);
							uni.request({
								url: this.$tempUrl + 'common/geocoderByLocation',
								data: { lat: latitude, lng: longitude },
								method: 'GET',
								success: res => {
									// console.log(res);
									const result = res.data.result;
									console.log('当前位置的区域code：' + result.ad_info.adcode);
									uni.request({
										url: this.$tempUrl + 'park/selectListByLocation',
										data: { location: result.ad_info.adcode, pageNum: '1', pageSize: '99' },
										method: 'GET',
										success: res => {
											const content = res.data.content;
											// console.log(content);
											// console.log(content.length);
											if (content.length === 0) {
												uni.request({
													url: this.$tempUrl + 'park/selectList',
													data: { pageNum: '1', pageSize: '99' },
													method: 'GET',
													success: res => {
														this.array = res.data.content;
													}
												});
											} else {
												this.array = content;
											}
										}
									});
								}
							});
						}
					});
				});
				wx.error(res => {
					console.log('wx err', res);
					uni.request({
						url: this.$tempUrl + 'park/selectList',
						data: { pageNum: '1', pageSize: '99' },
						method: 'GET',
						success: res => {
							this.array = res.data.content;
						}
					});
				});
			}
		});
	},
	methods: {
		doLogin() {
			// console.log(returnCitySN['cip']);
			uni.hideKeyboard();
			uni.showLoading({
				title: '提交中...'
			});
			setTimeout(() => {
				if (this.array.length === 0 || this.array[this.index].id === 0) {
					uni.hideLoading();
					uni.showToast({ title: '您还未选择园区', icon: 'none' });
				} else {
					uni.request({
						url: this.$tempUrl + 'app/selectListByParkId',
						data: { parkId: this.array[this.index].id, pageNum: '1', pageSize: '99' },
						method: 'GET',
						success: res => {
							// const data = res.data.content;
							// console.log(data);
							uni.hideLoading();
							uni.setStorageSync('park', this.array[this.index]);
							uni.showToast({ title: '登录成功', icon: 'success' });
							uni.reLaunch({
								url: '../tabBar/home'
							});
						}
					});
				}
			}, 1000);
		},
		bindPickerChange: function(e) {
			console.log('picker发送选择改变，携带值为', e.target.value);
			this.index = e.target.value;
		}
	}
};
</script>

